#!/bin/bash
dirArr=( /etc/bash-completion/ /usr/share/bash-completion/completions/   /usr/local/etc/bash_completion.d/  )


#copy smfc to one of the dirArr
_smfc_safe_copy(){
  dirname="${1}"
  res="0"
  
  for i in "${!dirArr[@]}"
  do
    if [ -d ${dirArr[$i]} ] 
    then
      sudo cp smfc ${dirArr[$i]}
      printf "smfc bash-completion has been installed.\n"
      res="1"
      break;
    fi
  done
  if [[ ${res} != "1" ]]; then
    printf "smfc bash-completion could not be installed.\n"
  fi
}

_smfc_safe_copy
