#!/bin/bash
fileArr=( /etc/bash-completion/smfc /usr/share/bash-completion/completions/smfc   /usr/local/etc/bash_completion.d/smfc  )


#copy smfc to one of the fileArr
_smfc_safe_remove(){
  dirname="${1}"
  res="0"
  
  for i in "${!fileArr[@]}"
  do
    if [ -f ${fileArr[$i]} ] 
    then
      sudo rm -rf ${fileArr[$i]}
      printf "smfc bash-completion has been removed.\n"
      res="1"
    fi
  done
  if [[ ${res} != "1" ]]; then
    printf "smfc bash-completion has already been removed.\n"
  fi
}

_smfc_safe_remove
