const globalData = require('../globalData');

function handle_arguments() {
	const platform = globalData.get('args.platform');
	handlePlatform(platform);
}

function handlePlatform(platform) {
	var task_handler;
	switch (platform) {
		case "win32":
			task_handler = require('./win32');
			break;
		case "c9":
		case "darwin":
		case "linux":
			task_handler = require('./c9');
			break;
	}
	task_handler.handle_task();
}

exports.handle_arguments = handle_arguments;
exports.handlePlatform = handlePlatform