//This module is used to decide which platform runs node.js
function JavaPathWithOs(version, callback){
	switch (process.platform){
		case 'win32':
			var win = require('./javapath_win.js');
			win = win.path(version,callback);
			break;
		case 'darwin':
			var mac = require('./javapath_mac.js');
			mac = mac.path(version,callback);
			break;
		case 'linux':
			var linux = require('./javapath_linux.js');
			linux = linux.path(version,callback);
			break;
		case 'freebsd':
			break;
		case 'sunos':
			break;
		default:
	}
}

exports.findJavaPath = JavaPathWithOs;