//This js file is used to trigger commands on linux and get the file path if the desired version exists
function JavaPathAndVersion(version,callback){
	var exec = require('child_process').exec,child;
	child = exec('java -version', function(error,stdout,stderr){
		if(error!=null){
			callback('Java Not Found in System',null);
		}
		else{
			if(stderr.toString().split('\n')[0].indexOf(version)>-1){
				child=exec('which java',function(error,stdout,stderr){
					if(error!=null){
						callback('Java Not Found in System',null);
					}
					else{
						var jpath = stdout.toString();
						jpath = jpath.substring(0,jpath.indexOf('bin')+3);
						callback(null,jpath);
					}
				});
			}
			else{
				callback('Java Not Found in System',null);
				}
		}
	});
}

exports.path = JavaPathAndVersion;


	







