//This js file is used to trigger commands on macOs and get the file path if the desired version exists
function JavaPathAndVersion(version,callback){
	var exec = require('child_process').exec,child;
	child = exec('/usr/libexec/java_home -v '+version, function(error,stdout,stderr){
		if(error!=null){
			callback('Java Not Found in System',null);
		}
		else{
			var jpath = stdout.toString();
			if(jpath.indexOf('Library')<=-1){
				callback('Java Not Found in System',null);
			}
			else{
				jpath = jpath.trim() + '/bin';
				callback(null,jpath);
			}
		}
	});
}

exports.path = JavaPathAndVersion;
	




