 const assert = require("chai").assert;

 const smartfacePluginValidator = require("smartface-plugin-validator");


 describe("smartfacePluginValidator, this module validates your smartface plugins ", function() {
     describe(" iOS Plugins", function() {
         var result;
         this.timeout(5000);
         before(function(done) {
             smartfacePluginValidator("./test/icloudios.zip", "iOS")
                 .then(function(res) {
                     if (res.error) {
                         console.log("\t - " + res.allErrors.join("\n\t - "));
                     }
                     result = res;
                     done();
                 }, function(err) {
                     console.error(err);
                     done(err);
                 });
         });
         it("should return object that has error as false.", function() {
             assert.isFalse(result.error);
         });
     });
     describe(" Android Plugins", function() {
         var result;
         this.timeout(5000);
         before(function(done) {
             smartfacePluginValidator("./test/ParseAndroid.zip", "Android")
                 .then(function(res) {
                     if (res.error) {
                         console.log("\t - " + res.allErrors.join("\n\t - "));
                     }
                     result = res;
                     done();
                 }, function(err) {
                     console.error(err);
                     done(err);
                 });
         });
         it("should return object that has error as false.", function() {
             assert.isFalse(result.error);
         });
     });
     //TODO more test case
 });